import React, { Component } from 'react'
import "../styles/styles.scss"
import ToggleView from '../components/Viewer.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isGrid: true,
    };
  }

  toggleGrid = () => {
    this.setState(state => ({ isGrid: !state.isGrid }));
  }
  render() {
    const hej = 'Heeeeej';
    return (
      <div>
        <ToggleView onClick={ this.toggleGrid }/>
        <main className={ this.state.isGrid ? "container" : "container single"}>
          <div className="item">1</div>
          <div className="item">2</div>
          <div className="item">3</div>
        </main>
      </div>
    )
  }
};

export default App;
