import React, { Component } from 'react'
// import { withRouter } from 'next/router'
import "../styles/styles.scss"
import ToggleGrid from '../components/Viewer.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isGrid: true,
    };
  }

  toggleGrid = (e) => {
    console.log('this.state: ', this.state);

    // e.preventDefault();
    // this.setState(state => ({ isGrid: !state.isGrid }));
    // console.log('hej1: ', this.state);

  }
  render() {
    const toggleGrid = this.props;
    console.log('render App: ', this.state);

    return (
      <div>
        <ToggleGrid onClick={ toggleGrid }/>
        <main className={ this.state.isGrid ? "container" : "container single"}>
          <div className="item">1</div>
          <div className="item">2</div>
          <div className="item">3</div>
        </main>
      </div>
    )
  }
};

export default App;
