import React, { Component } from 'react'
// import { withRouter } from 'next/router'
import "../styles/styles.scss"
import ToggleGrid from '../components/Viewer.js'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isGrid: true,
    };
    // this.onToggleGrid = this.onToggleGrid.bind(this);
  }

  onToggleGrid = this.onToggleGrid.bind(this);
  onToggleGrid(e) {
    e.preventDefault();
    this.setState(prevState => ({
      isGrid: !prevState.isGrid
    }));
  }
  render() {
    const {onToggleGrid} = this.props;
    const { isGrid } = this.state;
    console.log('toggleGrid: ', typeof this.onToggleGrid);

    return (
      <div>
        <ToggleGrid
          className='button'
          onClickHandler={ this.onToggleGrid}
          text={ isGrid ? "Grid view" : "Single view"}
          />
        <main className={ isGrid ? "container" : "container single"}>
          <div className="item">1</div>
          <div className="item">2</div>
          <div className="item">3</div>
        </main>
      </div>
    )
  }
};

export default App;
